 function Entrar() {
    var user = document.querySelector('.UserLoginInput').value;
    var password = document.querySelector('.PasswordLoginInput').value;

    const usuario = JSON.parse(localStorage.getItem(user))

    if (user == '' || password == '') {
        alert('Prencha todos os campos.')
        return;
    }

    if (usuario == null) {
        alert('Usuario incorreto.');
        return;
    }
        
    if(usuario.Senha == password) {
        location.href = '/postLogin/index.html';
        sessionStorage.setItem('webcache', JSON.stringify(usuario));
        return;
    } 

    else {
        alert('Dados incorretos.');
    }
}    

